mapper.update:
update TRBKUSPENGGUNA
set Q_sandi_ERROR = nvl(Q_sandi_ERROR,0)  + 1
WHERE I_PEG_NRK = #{namaPengguna}

update TRBKUSPENGGUNA
set c_lock = '1'
WHERE I_PEG_NRK = #{namaPengguna}

mapper.select:
select  nvl(to_date(d_sandi_expired, 'yyyymmdd') - trunc(sysdate), 0)   as sisahari
from TRBKUSPENGGUNA
where i_peg_nrk = #{namaPengguna}

SELECT A.I_PGUN as namaPengguna,trim(trailing ' ' from crypto16.DECRYPT(A.I_SANDI)) as passPengguna, A.C_AKTIF as kodeAktif,
'ROLE_'||A.C_PGUN_GROUP as kodeGrup,A.I_IDSKPD as idSkpd FROM TMPENGGUNA A WHERE A.I_PGUN = #{iPegNrk}
AND A.I_SANDI =  crypto16.ENCRYPT(#{passwd})
