mapper.select:
SELECT I_ID AS id, I_IDSKPD AS "skpd.idSkpd", V_SPP AS nilaiSpp, C_ANGG_TAHUN AS tahun, I_PGUN_REKAM AS idEntry, D_PGUN_REKAM AS tglEntry, I_PGUN_UBAH AS idEdit, D_PGUN_UBAH AS tglEdit, N_SKPD AS "skpd.namaSkpd" FROM (SELECT ROWNUM AS rn, a.* FROM (SELECT T.I_ID, T.I_IDSKPD, T.V_SPP, T.C_ANGG_TAHUN, T.I_PGUN_REKAM, T.D_PGUN_REKAM, T.I_PGUN_UBAH, T.D_PGUN_UBAH, S.N_SKPD FROM TRSPPPAGUUP T LEFT JOIN trskpd s ON S.I_ID = T.I_IDSKPD WHERE 1=1
and upper(S.N_SKPD) like '%'||upper(#{namaskpd})||'%'
and C_ANGG_TAHUN = #{tahun}
) a) WHERE ROWNUM <= #{limit} AND rn > #{offset}

SELECT count(distinct( T.I_ID)) FROM TRSPPPAGUUP T LEFT JOIN trskpd s ON S.I_ID = T.I_IDSKPD WHERE 1=1
and upper(S.N_SKPD) like '%'||upper(#{namaskpd})||'%'
and C_ANGG_TAHUN = #{tahun}

mapper.update:
UPDATE   TRSPPPAGUUP
SET   V_SPP = #{nilaiSpp},
I_PGUN_UBAH = #{idEdit},
D_PGUN_UBAH = #{tglEdit}
WHERE   I_ID = #{id}

mapper.delete:
UPDATE   TRSPPPAGUUP
SET   C_ANGG_TAHUN = #{tahunbaru} 
WHERE   C_ANGG_TAHUN = #{tahunlama}

mapper.update:
UPDATE TRSKPD SET
I_IDINDUK = #{idInduk}, C_SKPD = #{kodeSkpd}, C_UNITKERJA = #{kodeUnit}, N_SKPD = #{namaSkpd}, N_SKPD_PENDEK = #{namaSkpdPendek}, I_IDURUSAN = #{urusan.idUrusan}, C_AKTIF = #{isAktif}, C_PENDAPATAN = #{isPendapatan}, C_NERACA = #{isNeraca}, C_KELURAHAN = #{kodeKelurahan}, C_KECAMATAN = #{kodeKecamatan}, C_SKPD_LEVEL = #{levelSkpd}, C_KOMISI = #{kodeKomisi}, C_ASISTEN = #{kodeAsisten}, C_TAHUN_BERLAKU = #{tahunBerlaku}, C_TAHUN_BERAKHIR = #{tahunBerakhir}, I_PGUN_UBAH = #{idEdit}, D_PGUN_UBAH = #{tglEdit}
WHERE I_ID = #{idSkpd}
