(select sum(v_spd) from tmspdrincibtl where tmspdrincibtl.i_idspd = a.i_idspd) as V_SPD , d.i_nip_pkbtt AS i_nip_pkbtt, d.n_pkbtt AS i_n_pkbtt, d.i_nrk_pkbtt AS i_nrk_pkbtt, d.i_nip_pkblj AS i_nip_pkblj, d.n_pkblj AS i_n_pkblj, d.i_nrk_pkblj AS i_nrk_pkblj, d.E_PERATURAN_SPD1 as E_PERATURAN_SPD1, d.E_PERATURAN_SPD2 as E_PERATURAN_SPD2, d.E_PERATURAN_SPD5 as E_PERATURAN_SPD5, d.E_PERATURAN_SPD6 as E_PERATURAN_SPD6, '' AS i_nip_pkbantuan, '' AS i_n_pkbantuan, '' AS i_nrk_pkbantuan, '' AS i_nip_pkbiaya, '' AS i_n_pkbiaya, '' AS i_nrk_pkbiaya, c.d_spd_cetak, d.I_NIP_PA as i_nip_pa, d.N_PA as n_pa, d.I_NRK_PA as i_nrk_pa, q.c_akun as c_akun, ttd.n_peg AS n_peg, ttd.i_nip AS i_nip, ttd.n_jabatan AS n_jabatan, COALESCE (c.i_nrk, ttd.i_nrk ) AS i_nrk, c.c_status_cetak, 'BTL' AS jenis FROM vw_spd_btl_apbdp a LEFT JOIN tmspdcetak c ON c.i_id = a.i_idspd and a.c_angg_tahun = #{tahun} left JOIN trdokttd ttd ON ttd.c_aktif = 1 AND ( ttd.c_dok = 'SPD' AND (A.V_SPD >= TTD.V_DOK_MIN AND A.V_SPD <= TTD.V_DOK_MAX)) inner JOIN tmdpa d ON d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd} left join trbas q on q.c_tahun_berlaku = #{tahun} AND q.C_AKUN = '5.1.8' WHERE UPPER (status) != 'VALIDASI' AND a.c_angg_tahun = #{tahun} AND a.i_idskpd = #{idskpd} AND d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd}
UNION ALL SELECT a.i_idspd, a.i_idskpd, a.c_angg_tahun, a.d_spdno, a.i_spdno, a.status, (select sum(v_spd) from tmspdrincibtlbantuan where tmspdrincibtlbantuan.i_idspd = a.i_idspd) as V_SPD , '' AS i_nip_pkbtt, '' AS i_n_pkbtt, '' AS i_nrk_pkbtt, '' AS i_nip_pkblj, '' AS i_n_pkblj, '' AS i_nrk_pkblj, d.E_PERATURAN_SPD1 as E_PERATURAN_SPD1, d.E_PERATURAN_SPD2 as E_PERATURAN_SPD2, d.E_PERATURAN_SPD5 as E_PERATURAN_SPD5, d.E_PERATURAN_SPD6 as E_PERATURAN_SPD6, d.i_nip_pkbantuan AS i_nip_pkbantuan, d.n_pkbantuan AS i_n_pkbantuan, d.i_nrk_pkbantuan AS i_nrk_pkbantuan, '' AS i_nip_pkbiaya, '' AS i_n_pkbiaya, '' AS i_nrk_pkbiaya, c.d_spd_cetak, d.I_NIP_PA as i_nip_pa, d.N_PA as n_pa, d.I_NRK_PA as i_nrk_pa, '' as c_akun, ttd.n_peg AS n_peg, ttd.i_nip AS i_nip, ttd.n_jabatan AS n_jabatan, COALESCE (c.i_nrk, ttd.i_nrk ) AS i_nrk, c.c_status_cetak, 'BTL BANTUAN' AS jenis FROM vw_spd_btlbantuan_apbdp a LEFT JOIN tmspdcetak c ON c.i_id = a.i_idspd and a.c_angg_tahun = #{tahun} left JOIN trdokttd ttd ON ttd.c_aktif = 1 AND ( ttd.c_dok = 'SPD' AND (A.V_SPD >= TTD.V_DOK_MIN AND A.V_SPD <= TTD.V_DOK_MAX)) inner JOIN tmdpa d ON d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd} left join trbas q on q.c_tahun_berlaku = #{tahun} AND SUBSTR (q.c_akun, 1, 5) IN ('5.1.8.01.01') WHERE UPPER (status) != 'VALIDASI' AND a.c_angg_tahun = #{tahun} AND a.i_idskpd = #{idskpd} AND d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd}
UNION ALL SELECT a.i_idspd, a.i_idskpd, a.c_angg_tahun, a.d_spdno, a.i_spdno, a.status, (select sum(v_spd) from tmspdrincibl where tmspdrincibl.i_idspd = a.i_idspd) as V_SPD , '' AS i_nip_pkbtt, '' AS i_n_pkbtt, '' AS i_nrk_pkbtt, d.i_nip_pkblj AS i_nip_pkblj, d.n_pkblj AS i_n_pkblj, d.i_nrk_pkblj AS i_nrk_pkblj, d.E_PERATURAN_SPD1 as E_PERATURAN_SPD1, d.E_PERATURAN_SPD2 as E_PERATURAN_SPD2, d.E_PERATURAN_SPD5 as E_PERATURAN_SPD5, d.E_PERATURAN_SPD6 as E_PERATURAN_SPD6, '' AS i_nip_pkbantuan, '' AS i_n_pkbantuan, '' AS i_nrk_pkbantuan, '' AS i_nip_pkbiaya, '' AS i_n_pkbiaya, '' AS i_nrk_pkbiaya, c.d_spd_cetak, d.I_NIP_PA as i_nip_pa, d.N_PA as n_pa, d.I_NRK_PA as i_nrk_pa, '' as c_akun, ttd.n_peg AS n_peg, ttd.i_nip AS i_nip, ttd.n_jabatan AS n_jabatan, COALESCE (c.i_nrk, ttd.i_nrk ) AS i_nrk, c.c_status_cetak, 'BL' AS jenis FROM vw_spd_bl_apbdp a LEFT JOIN tmspdcetak c ON c.i_id = a.i_idspd and a.c_angg_tahun = #{tahun} left JOIN trdokttd ttd ON ttd.c_aktif = 1 AND ( ttd.c_dok = 'SPD' AND (A.V_SPD >= TTD.V_DOK_MIN AND A.V_SPD <= TTD.V_DOK_MAX)) inner JOIN tmdpa d ON d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd} left join trbas q on q.c_tahun_berlaku = #{tahun} AND SUBSTR (q.c_akun, 1, 5) IN ('5.1.8.01.01') WHERE UPPER (status) != 'VALIDASI' AND a.c_angg_tahun = #{tahun} AND a.i_idskpd = #{idskpd} AND d.c_angg_tahun = #{tahun} AND d.i_idskpd = #{idskpd}
