mapper.select:
SELECT idsp2d, nosp2d, skpdket,kode ,nilai FROM (SELECT ROWNUM AS rn, a.* FROM ( SELECT tmsp2d.i_id as idsp2d, tmsp2d.I_SP2DNO as nosp2d, trskpd.c_skpd || ' / ' || trskpd.N_SKPD as skpdket, (CASE WHEN tmspp.C_JENIS = '4' THEN '1-BIAYA (PPKD)' WHEN tmspp.C_JENIS = '2' THEN '2-BANTUAN (PPKD)' WHEN tmspp.C_JENIS ='1' AND trskpd.c_skpd_level='3' THEN '3-BTL-LS (PPKD)' WHEN tmspp.C_JENIS ='1' AND trskpd.c_skpd_level <> '3' THEN '4-BTL-LS (SKPD)' WHEN tmspp.C_JENIS ='3' AND trskpd.c_skpd_level <> '3' THEN '5-BL-LS (SKPD)' WHEN tmspp.C_JENIS ='3' AND tmspp.C_BEBAN <> 'LS' THEN '6-BL-UPGUTU (SKPD)' WHEN tmspp.C_UANGMUKA ='1'THEN '7-Uang Muka Kerja (PPKD)' ELSE '99' END ) AS kode, (CASE WHEN tmspp.C_JENIS = '4' THEN (select sum(v_spp) from tmspprincibiaya where i_idspp = tmspp.I_id) WHEN tmspp.C_JENIS = '2' THEN (select sum(v_spp) from tmspprincibtlbantuan where i_idspp = tmspp.I_id) WHEN tmspp.C_JENIS ='1' AND trskpd.c_skpd_level='3' THEN (select sum(v_spp) from tmspprincibtl where i_idspp = tmspp.I_id) WHEN tmspp.C_JENIS ='1' AND trskpd.c_skpd_level <> '3' THEN (select sum(v_spp) from tmspprincibtl where i_idspp = tmspp.I_id) WHEN tmspp.C_JENIS ='3' AND trskpd.c_skpd_level <> '3' THEN (select sum(v_spp) from tmspprincibl where i_idspp = tmspp.I_id) WHEN tmspp.C_JENIS ='3' AND tmspp.C_BEBAN <> 'LS' THEN (select sum(v_spp) from tmspprincibl where i_idspp = tmspp.I_id) WHEN tmspp.C_UANGMUKA ='1'THEN (select sum(v_spp) from tmspprincibl where i_idspp = tmspp.I_id) ELSE 0 END ) AS nilai FROM tmsp2d, trskpd, tmspp WHERE tmspp.I_id = tmsp2d.i_idspp AND tmspp.i_idskpd = trskpd.i_id AND tmsp2d.i_jour IS NULL AND tmsp2d.C_SP2D_SAH ='1' AND tmsp2d.c_angg_tahun = #{tahun} AND tmsp2d.C_WIL_SP2DPROSES = #{wilayah} AND to_char(tmsp2d.D_SP2D_SAH,'YYYYMMDD') = #{tglsah}
ORDER BY tmsp2d.D_SP2D_SAH, tmsp2d.i_id
) a) WHERE ROWNUM <= #{limit} AND rn > #{offset}
