SELECT count(*) as banyak from (
    SELECT DISTINCT i_journo_doc as noJurnal FROM tmjourskpd  order by i_journo_doc
)


SELECT  noJurnal
FROM   (SELECT   ROWNUM AS rn, a.*
FROM   (SELECT DISTINCT i_journo_doc as noJurnal FROM tmjourskpd where  i_idskpd= #{idskpd}
        order by i_journo_doc ) a)
WHERE   ROWNUM <= #{limit} AND rn > #{offset}

SELECT count(*) as banyak FROM (
    SELECT DISTINCT i_journo_doc as noJurnal FROM tmjourskpd where  i_idskpd= #{idskpd}
    order by i_journo_doc
)

SELECT  noJurnal
FROM   (SELECT   ROWNUM AS rn, a.*
FROM   (  SELECT  distinct i_journo_doc as noJurnal
from tmjourumumskpd where i_idskpd= #{idskpd} order by i_journo_doc
) a)
WHERE   ROWNUM <= #{limit} AND rn > #{offset}

SELECT count(*) as banyak FROM (
     SELECT  distinct i_journo_doc as noJurnal from tmjourumumskpd order by i_journo_doc
)
