mapper.select:
SELECT I_IDSPD AS idSpd, I_IDBL AS idBl, I_IDKEGIATAN AS "kegiatan.idKegiatan", C_KEGIATAN AS "kegiatan.kodeKegiatan", N_KEGIATAN AS "kegiatan.namaKegiatan", I_IDBAS AS "akun.idAkun", C_AKUN AS "akun.kodeAkun", N_AKUN AS "akun.namaAkun", V_SPD AS nilaiSpd, V_SPP AS nilaiSpp, V_SPPSEBELUM AS nilaiSppSebelum, V_SPPSISA AS nilaiSppSisa, I_SPDNO AS noSpd FROM (SELECT ROWNUM AS rn, a.* FROM ( SELECT XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, XXXX.I_IDKEGIATAN, TMDPAKEGIATAN.C_KEGIATAN, TMDPAKEGIATAN.N_KEGIATAN, I_IDBAS, C_AKUN, N_AKUN, SUM (V_SPD) AS V_SPD, SUM (V_SPPSEBELUM) AS V_SPPSEBELUM, SUM (V_SPD - V_SPPSEBELUM) AS V_SPPSISA, SUM (V_SPP) AS V_SPP, I_IDSPD, I_SPDNO, I_IDBL FROM (SELECT tmspd.c_angg_tahun, tmspd.I_IDSKPD AS I_IDSKPD, TMSPDRINCIBL.I_IDKEGIATAN AS I_IDKEGIATAN, TMSPDRINCIBL.I_IDBAS AS I_IDBAS, TMSPDRINCIBL.v_spd V_SPD, 0 AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBL.I_IDBL FROM tmspd, TMSPDRINCIBL, TMSPDSAH WHERE (tmspd.I_id = TMSPDRINCIBL.I_IDSPD) AND tmspd.I_ID = tmspdSAH.I_ID AND tmspd.c_angg_tahun = #{tahun} AND tmspd.c_jenis = '3' AND tmspd.i_idskpd = #{idskpd}
UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, V_SPP AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.I_ID <> #{idspp} AND TMSPP.i_idskpd = #{idskpd} AND TMSPD.I_ID = TMSPPRINCIBL.I_IDSPD AND EXISTS (SELECT 1 FROM TMSPPRINCIBL b WHERE b.I_IDKEGIATAN = TMSPPRINCIBL.I_IDKEGIATAN AND b.I_IDspp = #{idspp}) UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, 0 AS V_SPPSEBELUM, V_SPP AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.C_BEBAN = 'TU' AND TMSPP.I_ID = #{idspp} AND TMSPD.I_ID = TMSPPRINCIBL.I_idSPD AND TMSPP.i_idskpd = #{idskpd}) XXXX, TMDPAKEGIATAN, TRSKPD, TRBAS WHERE XXXX.I_IDSKPD = TRSKPD.I_ID AND XXXX.I_IDBAS = TRBAS.I_ID AND XXXX.I_IDKEGIATAN = TMDPAKEGIATAN.I_ID AND TMDPAKEGIATAN.c_angg_tahun = #{tahun}
AND TMDPAKEGIATAN.C_KEGIATAN LIKE '%'||#{kodekegiatan:VARCHAR}||'%'
AND I_SPDNO = #{spdno}
GROUP BY XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, XXXX.I_IDKEGIATAN, TMDPAKEGIATAN.C_KEGIATAN, TMDPAKEGIATAN.N_KEGIATAN, XXXX.I_IDBAS, C_AKUN, N_AKUN, I_IDSPD, I_SPDNO, I_IDBL) a) WHERE ROWNUM <= #{limit} AND rn > #{offset}

SELECT count(*) FROM (SELECT ROWNUM AS rn, a.* FROM ( SELECT XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, XXXX.I_IDKEGIATAN, TMDPAKEGIATAN.C_KEGIATAN, TMDPAKEGIATAN.N_KEGIATAN, I_IDBAS, C_AKUN, N_AKUN, SUM (V_SPD) AS V_SPD, SUM (V_SPPSEBELUM) AS V_SPPSEBELUM, SUM (V_SPD - V_SPPSEBELUM) AS V_SPPSISA, SUM (V_SPP) AS V_SPP, I_IDSPD, I_SPDNO, I_IDBL FROM (SELECT tmspd.c_angg_tahun, tmspd.I_IDSKPD AS I_IDSKPD, TMSPDRINCIBL.I_IDKEGIATAN AS I_IDKEGIATAN, TMSPDRINCIBL.I_IDBAS AS I_IDBAS, TMSPDRINCIBL.v_spd V_SPD, 0 AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBL.I_IDBL FROM tmspd, TMSPDRINCIBL, TMSPDSAH WHERE (tmspd.I_id = TMSPDRINCIBL.I_IDSPD) AND tmspd.I_ID = tmspdSAH.I_ID AND tmspd.c_angg_tahun = #{tahun} AND tmspd.c_jenis = '3' AND tmspd.i_idskpd = #{idskpd} UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, V_SPP AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.I_ID != #{idspp} AND TMSPP.i_idskpd = #{idskpd} AND TMSPD.I_ID = TMSPPRINCIBL.I_IDSPD AND EXISTS (SELECT 1 FROM TMSPPRINCIBL b WHERE b.I_IDKEGIATAN = TMSPPRINCIBL.I_IDKEGIATAN AND b.I_IDspp = #{idspp} ) UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, 0 AS V_SPPSEBELUM, V_SPP AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.C_BEBAN = 'TU' AND TMSPP.I_ID = #{idspp} AND TMSPD.I_ID = TMSPPRINCIBL.I_idSPD AND TMSPP.i_idskpd = #{idskpd} ) XXXX, TMDPAKEGIATAN, TRSKPD, TRBAS WHERE XXXX.I_IDSKPD = TRSKPD.I_ID AND XXXX.I_IDBAS = TRBAS.I_ID AND XXXX.I_IDKEGIATAN = TMDPAKEGIATAN.I_ID AND TMDPAKEGIATAN.c_angg_tahun = #{tahun}
AND TMDPAKEGIATAN.C_KEGIATAN LIKE '%'||#{kodekegiatan:VARCHAR}||'%'
AND I_SPDNO = #{spdno}
GROUP BY XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, XXXX.I_IDKEGIATAN, TMDPAKEGIATAN.C_KEGIATAN, TMDPAKEGIATAN.N_KEGIATAN, XXXX.I_IDBAS, C_AKUN, N_AKUN, I_IDSPD, I_SPDNO, I_IDBL) a)
