mapper.select:
SELECT I_IDSPD AS idSpd, I_IDBIAYA AS idBiaya, I_IDBAS AS "akun.idAkun", C_AKUN AS "akun.kodeAkun", N_AKUN AS "akun.namaAkun", V_SPD AS nilaiSpd, V_SPP AS nilaiSpp, V_SPPSEBELUM AS nilaiSppSebelum, V_SPPSISA AS nilaiSppSisa, C_SPD_STATUS AS statusSpd, I_SPDNO AS noSpd FROM (SELECT ROWNUM AS rn, a.* FROM ( SELECT XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, I_IDBAS, C_AKUN, N_AKUN, SUM (V_SPD) AS V_SPD, SUM (V_SPPSEBELUM) AS V_SPPSEBELUM, SUM (V_SPD - V_SPPSEBELUM) AS V_SPPSISA, SUM (V_SPP) AS V_SPP, I_IDSPD, I_SPDNO, C_SPD_STATUS, I_IDBIAYA FROM (SELECT tmspd.c_angg_tahun, tmspd.I_IDSKPD AS I_IDSKPD, TMSPDRINCIBIAYA.I_IDBAS AS I_IDBAS, TMSPDRINCIBIAYA.v_spd V_SPD, 0 AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBIAYA.C_SPD_STATUS, TMSPDRINCIBIAYA.I_IDBIAYA FROM tmspd, TMSPDRINCIBIAYA, TMSPDSAH WHERE (tmspd.I_id = TMSPDRINCIBIAYA.I_IDSPD) AND tmspd.I_ID = tmspdSAH.I_ID AND tmspd.c_angg_tahun = #{tahun} AND tmspd.c_jenis = '4' AND tmspd.i_idskpd = #{idskpd} UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBIAYA.I_IDBAS, 0 V_SPD, V_SPP AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBIAYA.C_SPD_STATUS, TMSPPRINCIBIAYA.I_IDBIAYA FROM TMSPP, TMSPPRINCIBIAYA, tmspd, TMSPDRINCIBIAYA WHERE (TMSPP.I_id = TMSPPRINCIBIAYA.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '4' AND TMSPP.i_idskpd = #{idskpd} AND TMSPP.I_ID < > #{idspp} AND TMSPD.I_ID = TMSPPRINCIBIAYA.I_IDSPD AND TMSPDRINCIBIAYA.I_idSPD = TMSPPRINCIBIAYA.I_idSPD AND TMSPD.i_idskpd = TMSPP.i_idskpd AND TMSPDRINCIBIAYA.I_IDBAS = TMSPPRINCIBIAYA.I_IDBAS UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBIAYA.I_IDBAS, 0 V_SPD, 0 AS V_SPPSEBELUM, V_SPP AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBIAYA.C_SPD_STATUS, TMSPPRINCIBIAYA.I_IDBIAYA FROM TMSPP, TMSPPRINCIBIAYA, tmspd, TMSPDRINCIBIAYA WHERE (TMSPP.I_id = TMSPPRINCIBIAYA.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '4' AND TMSPP.C_BEBAN = 'LS' AND TMSPP.I_ID = #{idspp} AND TMSPD.I_ID = TMSPPRINCIBIAYA.I_idSPD AND TMSPP.i_idskpd = #{idskpd} AND TMSPDRINCIBIAYA.I_idSPD = TMSPPRINCIBIAYA.I_idSPD AND TMSPD.i_idskpd = TMSPP.i_idskpd AND TMSPDRINCIBIAYA.I_IDBAS = TMSPPRINCIBIAYA.I_IDBAS ) XXXX,
TRSKPD, TRBAS WHERE XXXX.I_IDSKPD = TRSKPD.I_ID AND XXXX.I_IDBAS = TRBAS.I_ID
GROUP BY XXXX.c_angg_tahun, XXXX.I_IDSKPD, C_SKPD, N_SKPD, XXXX.I_IDBAS, C_AKUN, N_AKUN, I_IDSPD, I_SPDNO, C_SPD_STATUS, I_IDBIAYA) a) WHERE ROWNUM <= #{limit} AND rn > #{offset}

mapper.insert:
INSERT INTO TMSPP (I_ID, C_ANGG_TAHUN, C_BULAN, I_SPPNO, D_SPPNO, C_JENIS, C_BEBAN, I_IDSKPD, C_UANGMUKA, E_SPP, I_PPTK_NIP, N_PPTK, C_BANK, N_BANK, C_BANK_TRANSFER, N_BANK_TRANSFER, I_IDBANKPFK, I_REK_BANK, N_WP, N_TUJUAN, N_OBJEKPAJAK
) VALUES ( SEQ_TMSPP.NEXTVAL, #{tahun}, #{bulan}, #{noSpp}, #{tglSpp}, '4', 'LS', #{skpd.idSkpd}, #{kodeUangMuka}, UPPER(REPLACE ( REPLACE (REPLACE (#{keterangan} , CHR(13), ' '), CHR(10), ' '), CHR(9), ' ' )), #{nipPptk}, UPPER(#{namaPptk}), #{kodeBank},#{namaBankTransfer}, #{kodeBankTransfer},#{namaBankTransfer}, #{idBank}, regexp_replace(#{nomorRekBank},'[-. /]',''), UPPER(#{namaPenerima}), UPPER(#{namaYayasan}), UPPER(#{namaYayasan})
)
