mapper.select:
SELECT c_angg_tahun as tahun, i_idskpd as idSkpd, i_idspd as idSpd, i_spdno as noSpd, d_spdno as tglSpd, V_SPD+setoran_tu as nilaiSpd, v_sppsebelum as nilaiSppSebelum, v_kontrak as nilaiKontrak, sisa_spd as nilaiSpdSisa, V_SPP as nilaiSpp FROM (SELECT ROWNUM AS rn, a.* FROM ( SELECT xxx.c_angg_tahun, xxx. i_idskpd AS i_idskpd, i_idspd, i_spdno, d_spdno AS d_spdno, SUM (xxx.v_spd) v_spd, SUM (xxx.v_kontrak) v_kontrak, SUM (v_sppsebelum) v_sppsebelum, SUM (xxx.v_spd + setoran_tu - v_sppsebelum- v_kontrak) AS sisa_spd, SUM (v_spp) AS v_spp, SUM(setoran_tu) AS setoran_tu FROM (
SELECT c_angg_tahun, i_idskpd,i_idspd, tmspdrincibl.v_spd, 0 as v_kontrak, 0 v_sppsebelum, 0 AS v_spp, 0 AS setoran_tu FROM tmspd INNER JOIN tmspdrincibl ON tmspd.i_id = tmspdrincibl.i_idspd INNER JOIN tmspdsah ON tmspd.i_id = tmspdsah.i_id AND tmspd.c_jenis = '3' WHERE c_angg_tahun = #{tahun} AND i_idskpd = #{idskpd} and tmspdrincibl.c_spd_status != 'P'
UNION ALL SELECT c_angg_tahun, i_idskpd, i_idspd, 0 AS v_spd, v_kontr AS v_kontrak, 0 as v_sppsebelum,0 AS v_spp, 0 AS setoran_tu FROM tmkontrak WHERE c_angg_tahun = #{tahun} AND i_idskpd = #{idskpd}
UNION ALL SELECT tmspp.c_angg_tahun, i_idskpd,i_idspd, 0 AS v_spd, 0 as v_kontrak, v_spp AS v_sppsebelum, 0 AS v_spp, 0 AS setoran_tu FROM tmspp, tmspprincibl WHERE tmspp.c_angg_tahun = #{tahun} AND tmspp.c_jenis = '3' AND c_beban <> 'LS' AND tmspp.i_id = tmspprincibl.i_idspp AND i_idskpd = #{idskpd} AND tmspp.i_id <> #{idspp}
UNION ALL SELECT tmspp.c_angg_tahun, i_idskpd, i_idspd, 0 AS v_spd, 0 as v_kontrak, 0 AS v_sppsebelum, v_spp AS v_spp, 0 AS setoran_tu FROM tmspp, tmspprincibl WHERE tmspp.c_angg_tahun = #{tahun} AND tmspp.c_jenis = '3' AND c_beban = 'GU' AND tmspp.i_id = tmspprincibl.i_idspp AND i_idskpd = #{idskpd} AND tmspp.i_id = #{idspp}
UNION ALL SELECT A.c_angg_tahun, A.i_idskpd, A.i_idspd, 0 AS v_spd, 0 as v_kontrak, 0 AS v_sppsebelum, 0 AS v_spp, (v_kas_keluar-v_kas_terima) AS setoran_tu FROM tmbkuskpdpengeluaran A, tmspd B where A.i_idspd = B.i_id AND A.C_BEBAN = 'TU' and A.c_trx = 'ST' and A.c_jenis = 3 and A.c_angg_tahun = #{tahun} and A.i_idskpd = #{idskpd} and A.i_jour is not null and A.c_angg_tahun = B.c_angg_tahun and A.i_idskpd = B.i_idskpd
) xxx, tmspd ,tmspdsah WHERE xxx.i_idspd = tmspd.i_id and xxx.i_idspd = tmspdsah.i_id and tmspd.i_id =tmspdsah.I_id GROUP BY xxx.c_angg_tahun,xxx. i_idskpd,i_idspd, i_spdno, d_spdno) a)
WHERE sisa_spd <> 0 and ROWNUM <= #{limit} AND rn > #{offset}

SELECT I_IdSKPD, SUM (V_PAGUUP) AS PAGU_UP, SUM (V_SPP) AS TOTAL_SPP, SUM (V_SPJ) AS TOTAL_SPJ, SUM (V_PAGUUP - V_SPP + V_SPJ) AS PAGU_SPP, SUM(V_SPP-V_SPJ) AS SISA_UANG, ROUND (SUM (V_SPP-V_SPJ) / SUM (V_PAGUUP), 4) * 100 AS PERSEN FROM ( SELECT I_IdSKPD, V_SPP AS V_PAGUUP, 0 V_SPP, 0 V_SPJ FROM TRSPPPAGUUP WHERE ( #{tahun} between c_angg_tahun and TRSPPPAGUUP.C_TAHUN_BERAKHIR ) AND TRSPPPAGUUP.I_IDSKPD = #{idskpd} and c_aktif = 1
UNION ALL SELECT I_IdSKPD, 0 AS V_PAGUUP, V_SPP AS V_SPP, 0 V_SPJ FROM TMSPP, TMSPPRINCIBL WHERE TMSPP.I_ID = TMSPPRINCIBL.I_IDSPP AND TMSPP.C_ANGG_TAHUN = #{tahun} AND TMSPP.I_IDSKPD = #{idskpd} AND TMSPP.C_BEBAN IN ('UP', 'GU') and TMSPP.I_ID <> #{idspp}
UNION ALL SELECT I_IdSKPD, 0 AS V_PAGUUP, V_SPP AS V_SPP, 0 V_SPJ FROM TMSPP, TMSPPRINCIBTL WHERE TMSPP.I_ID = TMSPPRINCIBTL.I_IDSPP AND TMSPP.C_ANGG_TAHUN = #{tahun} AND TMSPP.I_IDSKPD = #{idskpd} AND TMSPP.C_BEBAN IN ('UP', 'GU') and TMSPP.I_ID <> #{idspp}
UNION ALL SELECT i_idskpd, 0 AS V_PAGUUP, 0 AS V_SPP, nvl((v_kas_keluar - v_kas_terima),0) as V_SPJ FROM tmbkuskpdpengeluaran where c_trx = 'JJ' and c_beban in ('UP', 'GU') and i_idskpd = #{idskpd} and c_angg_tahun = #{tahun} and i_jour is not null ) XXXX GROUP BY I_IdSKPD

SELECT SUM (V_SPD) AS V_SPD, SUM (V_SPP) AS V_SPP FROM (SELECT tmspd.c_angg_tahun, tmspd.I_IDSKPD AS I_IDSKPD, TMSPDRINCIBL.I_IDKEGIATAN AS I_IDKEGIATAN, TMSPDRINCIBL.I_IDBAS AS I_IDBAS, TMSPDRINCIBL.v_spd V_SPD, 0 AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPDRINCIBL.I_IDBL FROM tmspd, TMSPDRINCIBL, TMSPDSAH WHERE (tmspd.I_id = TMSPDRINCIBL.I_IDSPD) AND tmspd.I_ID = tmspdSAH.I_ID AND tmspd.c_angg_tahun = #{tahun} AND tmspd.c_jenis = '3' AND tmspd.i_idskpd = #{idskpd} UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, V_SPP AS V_SPPSEBELUM, 0 AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, TMSPM, TMSP2D, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPM.I_IDSPP = TMSPP.I_id AND TMSPM.I_ID = TMSP2D.I_idSPM AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.i_idskpd = #{idskpd} AND TMSPD.I_ID = TMSPPRINCIBL.I_IDSPD UNION ALL SELECT TMSPP.c_angg_tahun, TMSPP.I_IDSKPD, TMSPPRINCIBL.I_IDKEGIATAN, TMSPPRINCIBL.I_IDBAS, 0 V_SPD, 0 AS V_SPPSEBELUM, V_SPP AS V_SPP, TMSPD.I_ID AS I_IDSPD, I_SPDNO, TMSPPRINCIBL.I_IDBL FROM TMSPP, TMSPPRINCIBL, tmspd WHERE (TMSPP.I_id = TMSPPRINCIBL.I_IDSPP) AND TMSPP.c_angg_tahun = #{tahun} AND TMSPP.c_jenis = '3' AND TMSPP.C_BEBAN = 'GU' AND TMSPD.I_ID = TMSPPRINCIBL.I_idSPD AND TMSPP.i_idskpd = #{idskpd} AND TMSPP.I_ID = #{idspp}) XXXX, TMDPAKEGIATAN, TRSKPD, TRBAS WHERE XXXX.I_IDSKPD = TRSKPD.I_ID AND XXXX.I_IDBAS = TRBAS.I_ID AND XXXX.I_IDKEGIATAN = TMDPAKEGIATAN.I_ID AND TMDPAKEGIATAN.c_angg_tahun = #{tahun}
AND TMDPAKEGIATAN.C_KEGIATAN LIKE #{kodekegiatan}||'%'
AND I_SPDNO = #{spdno}

select count(c_beban) as banyak from tmspp 
where i_idskpd = #{idskpd}
and c_angg_tahun = #{tahun} 
and c_beban = 'UP'
and exists (select 1 from tmsp2d where i_idspp = tmspp.i_id and c_sp2d_sah = 1)

select to_char(count(*)+1) as banyak 
from tmsp2d, tmspp where tmsp2d.i_idspp= tmspp.i_id
and tmsp2d.c_angg_tahun = #{tahun} 
and tmsp2d.i_idskpd = #{idskpd}
and c_beban = 'GU'
and c_sp2d_sah = 1
